import api from "../../services/api"
import React, { useState } from "react"


const DeletePokemonIdDb =() => {

    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status,SetStatus] = useState (false)

    const DeletePokemonsById = () => {     
        api.delete(`/pokemon-delete-db/${id}/`,{
            headers: {
                'Content-Type': 'application/json',
              },
        })
            .then((response) => {
               const data = response.data
               SetStatus(true)
               SetError(false)               
            }).catch((err)=>{
                if(err.response){
                    SetError(true)      
                }
            });
        }
    return(    

        <div>
        <h1>Bem vindo a pokedex </h1>
        <p>digite o id do pokemon que deseja deletar:</p>
        <input placeholder="id do pokemob" type="number" value={id} onChange={e => Setid(e.target.value)}/>
        <button type="button" onClick={DeletePokemonsById}>enviar</button>
        {status?
        <div>
            {!error?
            <div>
            <h1>Pokemon deletado com sucesso</h1>
            </div>
            :
            <div>
                <h1>Opss.. nenhum pokemon encontrado para deletar</h1>
            </div>
            }
        </div>
        :null}
    </div>




    )




}



export default DeletePokemonIdDb
