import api from "../../services/api"
import React, { useState,useEffect } from "react"



const [data, setData] = useState([])

const GetOnePokemonInWeb = () => {


    useEffect(() => {
        api.get(`/pokemon/pokemon-in-web/1/`)
            .then((response) => {
                const data = response.data
                setData(data)

            });
    }, []);







    return (

        <div>
        <div>
            <h1>Lista de pokemons salvos no banco</h1>
            {data.map((item) => {
                const { id, name, weight, baseExperience, idPokemon } = item;
                return (
                    <div key={id}>
                        <h1>Id pokemon: {id}</h1>
                        <p>Nome do pokemon: {name}</p>
                        <p>Peso do pokemon {weight}</p>
                        <p>Base de experiencia:{baseExperience}</p>
                        <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${idPokemon}.png`} />
                    </div>
                );

            })}

        </div>
    </div>




    )
}

export default GetOnePokemonInWeb;
