import api from "../../services/api"
import React, { useState } from "react"

const SavePokemonInDatabase = () =>{

    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status,SetStatus] = useState (false)

    const SavePokemonsById = () => {     
        api.post(`/pokemon/pokemon-delete-db/${id}/`)
            .then((response) => {
               const data = response.data
               SetStatus(true)
               SetError(false)               
            }).catch((err)=>{
                if(err.response){
                    SetError(true)      
                }
            });
        }
    return(    

        <div>
        <h1>Bem vindo a pokedex </h1>
        <p>digite o id do pokemon que deseja salvar:</p>
        <input placeholder="id do pokemob" type="number" value={id} onChange={e => Setid(e.target.value)}/>
        <button type="button" onClick={SavePokemonsById}>enviar</button>
        {status?
        <div>
            {!error?
            <div>
            <h1>Pokemon de salvo com sucesso</h1>
            </div>
            :
            <div>
                <h1>Opss.. Pokemon ja salvo</h1>
            </div>
            }
        </div>
        :null}
    </div>




    )




}

export default SavePokemonInDatabase;
