const SavePokemonInDatabase = () =>{

    const [id, Setid] = useState()
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [baseExperience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [error, SetError] = useState(false)

    const SavePokemonsById = () => {     
        api.get(`/pokemon/get-one-database/${id}/`)
            .then((response) => {
               const data = response.data
               const corpo =  data.body;
               const status = data.statusCodeValue;
               console.log(data);
               console.log(status);
               SetError(false)
               SetidPokemon(corpo.idPokemon);
               Setname(corpo.name);
               SetbaseExperience(corpo.baseExperience);
               Setweight(corpo.weight);

            }).catch((err)=>{
                if(err.response){
                    SetError(true)      
                }
            });
        }
    return(    

        <div>
        <h1>Bem vindo a pokedex </h1>
        <p>digite o id do pokemon:</p>
        <input placeholder="Senha" type="number" value={id} onChange={e => Setid(e.target.value)}/>
        <button type="button" onClick={SavePokemonsById}>enviar</button>
        {!error?
        <div>
        <h1>Pokemon de {id} salvo com sucesso</h1>
        </div>
        :
        <div>
            <h1>Opss.. Erro ao salvar pokemon</h1>
        </div>

        }
    </div>




    )




}

export default SavePokemonInDatabase;
