import api from "../../services/api"
import React, { useState } from "react"




const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [base_experience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status, SetStatus] = useState(false)
    const [showAbilities,setShowAbilities] = useState(false)


    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${id}/`)
            .then((response) => {
                console.log(response.data)
                SetError(false)
                SetStatus(true)
                const {
                    name,
                    id,
                    weight,
                    base_experience,
                    moves,
                    abilities,

                } = response.data;
                SetAbilities(abilities.map((ability) =>
                    ability.ability.name
                        .toLowerCase()

                ));

                SetMoves(moves.map((move) =>
                    move.move.name
                        .toLowerCase()


                ));

                SetidPokemon(id);
                Setname(name);
                SetbaseExperience(base_experience);
                Setweight(weight);

            }).catch((err) => {
                if (err.response) {
                    SetError(true)
                }
            });
    }


    const handlerAbilities = () => {
        SetAbilities(true)
        console.log("aaaaaa")
        return


    }


    return (


        <div>
            <div className="card">
                <input className="pesquisar" placeholder="ou nome do pokemon" type="text" value={id} onChange={e => Setid(e.target.value)} />
                <button className="pesquisar" type="button" onClick={getPokemonsById}>Pesquisar</button>
            </div>
            {status ?
                <div className="card">
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${idPokemon}.png`} className="imgweb" />
                    <ul className="info">Id do pokemon: {idPokemon} </ul>
                    <ul className="info">Nome do Pokemon:{name} </ul>
                    <ul className="info">Base de experiencia:{base_experience} </ul>
                    <ul className="info">Peso: {weight} </ul>
                    <button className="info" type="button" onClick={handlerAbilities} >Habilidades</button>

                </div>
                : null}
            <div>
                {error ?
                    <div>
                        <h1>Opss.. parece que voce digitou um id inesxistente</h1>
                    </div>
                    : null}
            </div>
            {showAbilities?
            <div>          
                    <ul>{abilitiesPokemon}</ul>
            </div>
            :null}

        </div>





    )
}

export default GetOnePokemonInWeb;
