import api from "../../services/api"
import React, { useState } from "react"


const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);

    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/13/`)
            .then((response) => {
                const {
                    name,
                    id,
                    weight,
                    base_experience,
                    moves,
                    abilities,

                } = response.data;
                SetAbilities(
                    abilities
                        .map((ability) =>
                            ability.ability.name
                                .toLowerCase()
                                .split("-")
                                .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
                                .join(" ")
                        ));

                SetMoves(
                    moves
                        .map((move) =>
                            move.move.name
                                .toLowerCase()
                                .split("-")
                                .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
                                .join(" ")
                        ));



            })
    }




    return (

        <div>
            <h1>pokemon na web</h1>
            <button onClick={getPokemonsById}>teste</button>
            <h1>{abilitiesPokemon}</h1>
            <h1>{movesPokemon}</h1>
        </div>




    )
}

export default GetOnePokemonInWeb;
