        api.get(`/pokemon/get-one-database/${id}/`)
            .then((response) => {
               const data = response.data
               SetError(false)               
            }).catch((err)=>{
                if(err.response){
                    SetError(true)      
                }
            });
        }
    return(    

        <div>
