import api from "../../services/api"
import React, { useState } from "react"


const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idInput, SetidInput] = useState()
    const [error, SetError] = useState(false)


    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${idInput}/`)
            .then((response) => {
                const status = response.statusCodeValue;
                const {
                    name,
                    id,
                    weight,
                    base_experience,
                    moves,
                    abilities,

                } = response.data;
                if (status === 200)
                { SetAbilities(abilities
                                .map((ability) =>
                                    ability.ability.name
                                        .toLowerCase()
                                        .split("-")
                                        .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
                                        .join(" ")
                                ));

                        SetMoves(
                            moves
                                .map((move) =>
                                    move.move.name
                                        .toLowerCase()
                                        .split("-")
                                        .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
                                        .join(" ")
                                ));
                }else{
                    console.log("erro")
                    SetError(true)
                }
            })
    }




    return (

        <div>

            <p>digite o id do pokemon para encontrarmos na PokeAPI:</p>
            <input placeholder="Senha" type="number" value={idInput} onChange={e => SetidInput(e.target.value)} />
            <button type="button" onClick={getPokemonsById}>enviar</button>
            {!error?
            <div>
            <h1>Habilidades:</h1>
            <h1>{abilitiesPokemon}</h1>
            <h1>Movimentos:</h1>
            <h1>{movesPokemon}</h1>
            </div>
            :
            <div>
                <h1>Opss.. parece que voce digitou um id inesxistente</h1>
            </div>

            }
        </div>




    )
}

export default GetOnePokemonInWeb;
