import api from "../../services/api"
import React, { useState,useEffect } from "react"




const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [base_experience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status, SetStatus] = useState(false)
    const [showAbilities,setShowAbilities] = useState(false)
    const [showMoves,setShowMoves] = useState(false)




    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${id}/`)
            .then((response) => {
                const data = response.data
                setData(data)

            }).catch((err) => {
                if (err.response) {
                    SetError(true)
                    setShowAbilities(false)
                    setShowMoves(false)
                    SetStatus(false)
                }
            });
    }


    const handlerAbilities = () => {
        setShowAbilities(true)
        return


    }

    const handlerMoves = () => {
        setShowMoves(true)
        return


    }


    return (


        <div>
            <div className="cardpesquisa">
                <input className="pesquisar" placeholder="ou nome do pokemon" type="text" value={id} onChange={e => Setid(e.target.value)} />
                <button className="pesquisar" type="button" onClick={getPokemonsById}>Pesquisar</button>
            </div>
            {status ?
                <div className="card">
                    {data.map((item) => {
                    const { id, name, weight, baseExperience, idPokemon } = item;
                    return (
                        <div key={id}>
                            <h1>Id pokemon: {id}</h1>
                            <p>Nome do pokemon: {name}</p>
                            <p>Peso do pokemon {weight}</p>
                            <p>Base de experiencia:{baseExperience}</p>
                            <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${idPokemon}.png`} />
                        </div>
                    );

                })}
                    <button className="info" type="button" onClick={handlerAbilities} >Habilidades</button>
                    <button className="info" type="button" onClick={handlerMoves}>Movimentos</button>

                </div>
                : null}
            <div>
                {error ?
                    <div>
                        <h1>Opss.. parece que voce digitou um id inesxistente</h1>
                    </div>
                    : null}
            </div>
            {showAbilities?
            <div className="cardinfo">   
             <h2>Lista de abilidades:</h2>       
                    <ul>{abilitiesPokemon}</ul>
            </div>
            :null}
            <div>
                {showMoves?
                <div className="cardinfo">
                        <h2>Lista de movimentos:</h2>
                     <ul>{movesPokemon}</ul>
                </div>
                :null}
            </div>

        </div>





    )
}

export default GetOnePokemonInWeb;
