import api from "../../services/api"
import React, { useState, useEffect } from "react"




const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [base_experience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status, SetStatus] = useState(false)
    const [showAbilities, setShowAbilities] = useState(false)
    const [showMoves, setShowMoves] = useState(false)



    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${id}/`)
            .then((response) => {
                console.log(response.data)
                SetError(false)
                SetStatus(true)
                const {name, id,weight,base_experience, moves,   abilities,
                } = response.data;
                SetidPokemon(id);
                Setname(name);
                SetbaseExperience(base_experience);
                Setweight(weight);
                setShowAbilities(false)
                setShowMoves(false)
                SetMoves(moves)
                SetAbilities(abilities)

            }).catch((err) => {
                if (err.response) {
                    SetError(true)
                    setShowAbilities(false)
                    setShowMoves(false)
                    SetStatus(false)
                }
            });
    }

    const handlerAbilities = () => {
        setShowAbilities(true)
        setShowMoves(false)
        return


    }

    const handlerMoves = () => {
        setShowMoves(true)
        setShowAbilities(false)
        return


    }


    return (


        <div>
            <div className="cardpesquisa">
                <input className="pesquisar" placeholder="ou nome do pokemon" type="text" value={id} onChange={e => Setid(e.target.value)} />
                <button className="pesquisar" type="button" onClick={getPokemonsById}>Pesquisar</button>
            </div>
            {status ?
                <div className="card">
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${idPokemon}.png`} className="imgweb" />
                    <ul className="info">Id do pokemon: {idPokemon} </ul>
                    <ul className="info">Nome do Pokemon:{name} </ul>
                    <ul className="info">Base de experiencia:{base_experience} </ul>
                    <ul className="info">Peso: {weight} </ul>
                    <button className="info" type="button" onClick={handlerAbilities} >Habilidades</button>
                    <button className="info" type="button" onClick={handlerMoves}>Movimentos</button>

                </div>
                : null}
            <div>
                {error ?
                    <div>
                        <h1 className="dadosPokemon">Este pokemon não existe</h1>
                        <img className="imgweb" src="https://pm1.narvii.com/6375/69386816c2d10aace9de3c73e9fe60f059eb5cc7_hq.jpg" width="200px"/>
                    </div>
                    : null}
            </div>
            {showAbilities ?
                <div className="cardinfo">
                    <h2>Lista de Habilidades:</h2>
                        {abilitiesPokemon.map((ability, index) => {
                            console.log(ability.ability.name)
                            return (
                                <div key={index}>
                                    <ul> {index +1} - {ability.ability.name}</ul>
                                </div>
                            );
                        })}
                </div>
                : null}
            <div>
                {showMoves ?
                    <div className="cardinfo">
                        <h2>Lista de movimentos:</h2>
                        {movesPokemon.map((move, index) => {
                            console.log(move.move.name)
                            return (
                                <div key={index}>
                                    <ul> {index +1} - {move.move.name}</ul>
                                </div>
                            );
                        })}
                    </div>
                    : null}
            </div>

        </div>





    )
}

export default GetOnePokemonInWeb;
