import React, { useState } from "react"






const Card = () => {


    const [id, SetId] = useState()
    const [data, Setdata] = useState()


    //  api.get(`/pokemon/get-one-database/${id}/`)
    // .then((response) => {
    //      r


    //  })

    const getPokemonsById = e => {
        e.preventDefault()
        axios(`http://localhost:8080/get-one-database/${id}/`, {
            mode: "no-cors",
            method: "GET",
            headers: {
                "Accept": "application/json"
            }
        }).then((response) => {
            console.log(response.body); // null
            return dispatch({
                type: "GET_CALL",
                response: response
            });
        })
            .catch(error => { console.log('request failed', error); });




    }


    return (

        <div>
            <h1>Bem vindo a pokedex </h1>
            <p>digite o id do pokemon:</p>
            <input placeholder="Senha" type="password" value={id} onChange={e => SetId(e.target.value)} />
            <button type="button" onClick={getPokemonsById}>enviar</button>
            <p>{data}</p>
        </div>



    );
}


export default Card;
