import api from "../services/api"
import React, { useState } from "react"





const Card = () => {


    const [id, SetId] = useState()
    const [data, Setdata] = useState()


    interface dto {
        baseExperience: string
    }

    const getPokemonsById = e => {
        e.preventDefault()
        api.get(`/pokemon/get-one-database/${id}/`)
            .then((response) => {
                const data = response.data
                dto.baseExperience = data.baseExperience
                console.log(dto.baseExperience = data.baseExperience)
            })

    }


    return (

        <div>
            <h1>Bem vindo a pokedex </h1>
            <p>digite o id do pokemon:</p>
            <input placeholder="Senha" type="password" value={id} onChange={e => SetId(e.target.value)}/>
            <button type="button" onClick={getPokemonsById}>enviar</button>
            <p>{data}</p>
        </div>



    );
}


export default Card;
