import api from "../../services/api"
import React, { useState } from "react"




const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [baseExperience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [id, Setid] = useState()
    const [error, SetError] = useState(false)
    const [status, SetStatus] = useState(false)


    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${id}/`)
            .then((response) => {
                SetError(false)
                SetStatus(true)
                const {
                    name,
                    id,
                    weight,
                    baseExperience,
                    moves,
                    abilities,

                } = response.data;
                SetAbilities(abilities.map((ability) =>
                    ability.ability.name
                        .toLowerCase()

                ));

                SetMoves(moves.map((move) =>
                    move.move.name
                        .toLowerCase()


                ));

                SetidPokemon(id);
                Setname(name);
                SetbaseExperience(baseExperience);
                Setweight(weight);

            }).catch((err) => {
                if (err.response) {
                    SetError(true)
                }
            });
    }


    return (

        <div className="ContainerOne">
            <p className="pesquisa">digite o id ou nome do pokemon para encontrarmos na PokeAPI:</p>
            <input placeholder="ou nome do pokemon" type="text" value={id} onChange={e => Setid(e.target.value)} />
            <button type="button" onClick={getPokemonsById}>enviar</button>

            <div>
                {status?
                <div className="Pokemonweb">
                    <h2 className="dadosPokemon">Dados sobre o pokemon</h2>
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${idPokemon}.png`} className="imgweb" />
                    <ul className="info">Id do pokemon: {idPokemon} </ul>
                    <ul className="info">Nome do Pokemon:{name} </ul>
                    <ul className="info">Base de experiencia:{baseExperience} </ul>
                    <ul className="info">Peso: {weight} </ul>

                </div>
                :null}
                <div>
                    {error?
                <div>
                    <h1>Opss.. parece que voce digitou um id inesxistente</h1>
                </div>
                :null}
                </div>

            </div>

        </div>




    )
}

export default GetOnePokemonInWeb;
