import api from "../../services/api"
import React, { useState } from "react"


const GetOnePokemonInWeb = () => {

    const [abilitiesP, SetAbilities] = useState([]);

    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/10/`)
            .then((response) => {
                const {
                    name,
                    id,
                    weight,
                    base_experience,
                    moves,
                    abilities,

                } = response.data;
                SetAbilities(
                    abilities
                        .map((ability) =>
                            ability.ability.name
                                .toLowerCase()
                                .split("-")
                                .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
                                .join(" ")
                        ));
            })
    }




    return (

        <div>
            <h1>pokemon na web</h1>
            <button onClick={getPokemonsById}>teste</button>
            <h1>{abilitiesP}</h1>
        </div>




    )
}

export default GetOnePokemonInWeb;
