import api from "../services/api"
import React, { useState } from "react"





const Card = () => {


    const [id, SetId] = useState()
    const [data, Setdata] = useState()


    const getPokemonsById = () => {

        api.get(`/pokemon/get-one-database/${id}/`)
            .then((response) => {
                const data = response.data
                console.log(data)
                Setdata(data)
            }).catch((err) => {
                if (err.response) {
                    console.log(err.response)
                    alert('algo deu errado')
                    window.location.reload()
                } else {
                    alert('Servidor em manutenção')
                    window.location.reload()
                }
            })

    }


    return (

        <div>
            <h1>Bem vindo a pokedex </h1>
            <p>digite o id do pokemon:</p>
            <input placeholder="Senha" type="password" value={id} onChange={e => SetId(e.target.value)}/>
            <button type="button" onClick={getPokemonsById}>enviar</button>
            <label>{data}</label>
        </div>



    );
}


export default Card;
