import api from "../../services/api"
import React, { useState } from "react"


const GetOnePokemonInWeb = () => {

    const [abilitiesPokemon, SetAbilities] = useState([]);
    const [movesPokemon, SetMoves] = useState([]);
    const [idPokemon, SetidPokemon] = useState()
    const [name, Setname] = useState()
    const [baseExperience, SetbaseExperience] = useState()
    const [weight, Setweight] = useState()
    const [id, Setid] = useState()
    const [error, SetError] = useState(false)


    const getPokemonsById = () => {
        api.get(`/pokemon/pokemon-in-web/${id}/`)
            .then((response) => {
                const status = response.status
                const data = response.data
                console.log(response);
                SetError(false)
                const {
                    name,
                    id,
                    weight,
                    baseExperience,
                    moves,
                    abilities,

                } = response.data;
                if (status === 200) {
                SetAbilities(abilities.map((ability) =>
                    ability.ability.name
                        .toLowerCase()

                ));

                SetMoves(moves.map((move) =>
                    move.move.name
                        .toLowerCase()


                ));
                SetidPokemon(id);
                Setname(name);
                SetbaseExperience(baseExperience);
                Setweight(weight);
            }else{
                console.log("erro")
                SetError(true)
            }

            })
    }


    return (

        <div>

            <p>digite o id do pokemon para encontrarmos na PokeAPI:</p>
            <input placeholder="Senha" type="number" value={id} onChange={e => Setid(e.target.value)} />
            <button type="button" onClick={getPokemonsById}>enviar</button>

            {!error?
            <div>
                <h1>Dados sobre o pokemon</h1>
                <ul>Id do pokemon: {idPokemon} </ul>
                <ul>Nome do Pokemon:{name} </ul>
                <ul>Base de experiencia:{baseExperience} </ul>
                <ul>Peso: {weight} </ul>
                <h1>Habilidades:</h1>
                <ul>{abilitiesPokemon}</ul>
                <h1>Movimentos:</h1>
                <ul>{movesPokemon}</ul>
            </div>
            :
            <div>
                <h1>Opss.. parece que voce digitou um id inesxistente</h1>
            </div>
                }

        </div>




    )
}

export default GetOnePokemonInWeb;
